class Solution:
    def findSubstringInWraproundString(self, p):
        l, lo = {}, 0
        for hi in range(0, len(p)):
            if hi > 0 and (p[hi - 1] + p[hi]) not in 'abcdefghijklmnopqrstuvwxyza':
                lo = hi
            l[p[hi]] = max(hi - lo + 1, l.get(p[hi], 0))
        return sum(l.values())


if __name__ == '__main__':
    solution = Solution()
    print(solution.findSubstringInWraproundString(
        ""))
